import { useEffect } from "react";
import { useState } from "react"
import "./sidebar.css"


export default function Sidebar() {
    const[cat , setCats] = useState([]);

    useEffect(()=>{
        const getCats= async ()=>{
            const res = await axios.get("/categories")
            setCats(res.data);
        }
        getCats();
    },[]);
  return (
    <div className="sidebar">
        <div className="sidebarItem">
            <span className="sidebarTitle">About Me</span>
            <img 
            src="https://www.html.am/images/html-codes/links/boracay-white-beach-sunset-300x225.jpg"
            alt="" 
            />
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Tempore repellendus unde
               placeat asperiores quo debitis tenetur accusamus pariatur eum numquam, voluptates 
               aliquid itaque velit fugiat eveniet possimus nemo odit corrupti.
               </p>
        </div>
        <div className="sidebarItem">
            <span className="sidebarTitle">Categories</span>
            <ul className="sidebarList">
                <li className="sidebarListItem">Life</li>
            </ul>
        </div>
        <div className="sidebarItem">
            <span className="sidebarTitle">Follow Us</span>
            <div className="sidebarSocial">
            <i className="sidebarIcon fa-brands fa-square-facebook"></i>
            <i className="sidebarIcon fa-brands fa-twitter"></i>
            <i className="sidebarIcon fa-brands fa-instagram"></i>
            <i className="sidebarIcon fa-brands fa-pinterest"></i>
            </div>
        </div>
    </div>
  )
}
